INSERT INTO `wp_posts` VALUES (1,1,'2023-02-27 16:40:13','2023-02-27 16:40:13','<!-- wp:paragraph -->\n<p>Welcome to WordPress. This is your first post. Edit or delete it, then start writing!</p>\n<!-- /wp:paragraph -->','Hello world!','','publish','open','open','','hello-world','','','2023-02-28 10:51:42','2023-02-28 10:51:42','',0,'http://localhost/directday/?p=1',0,'post','',1),(2,1,'2023-02-27 16:40:13','2023-02-27 16:40:13','<!-- wp:columns {\"verticalAlignment\":\"center\",\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide are-vertically-aligned-center\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:group {\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\"}} -->\n<div class=\"wp-block-group\"><!-- wp:paragraph {\"style\":{\"color\":{\"text\":\"#000000ba\"},\"typography\":{\"lineHeight\":1.4}}} -->\n<p class=\"has-text-color\" style=\"color:#000000ba;line-height:1.4\">This company was created to provide speed and accuracy to<br>businesses with the help of technology. a solution that can be<br>provided in this company helps enterprises to use integrated</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<div class=\"showMoreSliderDesc directday-blue\"><span>more</span><span>&nbsp;</span><li class=\"fa fa-angle-down\"></li></div>\n<!-- /wp:html -->\n\n<!-- wp:spacer {\"height\":\"48px\"} -->\n<div style=\"height:48px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"style\":{\"typography\":{\"textTransform\":\"uppercase\",\"fontStyle\":\"normal\",\"fontWeight\":\"700\",\"fontSize\":\"28px\"},\"color\":{\"text\":\"#000000ba\"}}} -->\n<h2 class=\"has-text-color\" style=\"color:#000000ba;font-size:28px;font-style:normal;font-weight:700;text-transform:uppercase\">A BOON TO YOUR BUSINESS.</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"color\":{\"text\":\"#000000ba\"}},\"fontSize\":\"extra-small\"} -->\n<p clas